const btnToggle = document.querySelector(".btn_nav_toggle");
const navMenu = document.querySelector(".nav__menu");

btnToggle.addEventListener("click", () => {
    const e = navMenu.getAttribute("data-visible");

    if ("false" === e) {
        navMenu.setAttribute("data-visible", !0);
        btnToggle.setAttribute("aria-expanded", !0);
    } else if ("true" === e) {
        navMenu.setAttribute("data-visible", !1);
        btnToggle.setAttribute("aria-expanded", !1);
    }
});
